from rest_framework import serializersfrom info_rest.serializers import DishSerializerfrom cart.models import Order, OrderItemclass OrderSerializer(serializers.ModelSerializer):    class Meta:        model = Order        fields = ['city', 'address']class OrderItemsSerializer(serializers.ModelSerializer):    order = OrderSerializer()    product = DishSerializer()    class Meta:        model = OrderItem        fields = ['order', 'product', 'price', 'quantity']